mapper.select:
SELECT SUM(CASE WHEN MONTH(business_Hours_start) =1 THEN 1 ELSE 0 END) january, SUM(CASE WHEN MONTH(business_Hours_start) =2 THEN 1 ELSE 0 END) february, SUM(CASE WHEN MONTH(business_Hours_start) =3 THEN 1 ELSE 0 END) march, SUM(CASE WHEN MONTH(business_Hours_start) =4 THEN 1 ELSE 0 END) april, SUM(CASE WHEN MONTH(business_Hours_start) =5 THEN 1 ELSE 0 END) may, SUM(CASE WHEN MONTH(business_Hours_start) =6 THEN 1 ELSE 0 END) june, SUM(CASE WHEN MONTH(business_Hours_start) =7 THEN 1 ELSE 0 END) july, SUM(CASE WHEN MONTH(business_Hours_start) =8 THEN 1 ELSE 0 END) august, SUM(CASE WHEN MONTH(business_Hours_start) =9 THEN 1 ELSE 0 END) september, SUM(CASE WHEN MONTH(business_Hours_start) =10 THEN 1 ELSE 0 END) october, SUM(CASE WHEN MONTH(business_Hours_start) =11 THEN 1 ELSE 0 END) november, SUM(CASE WHEN MONTH(business_Hours_start) =12 THEN 1 ELSE 0 END) december FROM company where year(business_Hours_start) = #{year} and del_flag = '0'
AND business_Hours_start >= #{dateStart}
AND #{dateEnd} >= business_Hours_start

select count(*) 
from company where del_flag = '0'

	 AND business_Hours_start >= #{dateStart}


	 AND  #{dateEnd} >=  business_Hours_start


   AND EXTRACT(YEAR FROM business_Hours_start) = #{dateYear}

select count(*)
from company
where company_state like concat('%',#{companyState},'%')  and del_flag = '0'

	 AND business_Hours_start >= #{dateStart}


	 AND  #{dateEnd} >=  business_Hours_start


   AND EXTRACT(YEAR FROM business_Hours_start) = #{dateYear}

	select industry_involved from company
 	where del_flag = '0' 

		 AND business_Hours_start >= #{dateStart}


		 AND  #{dateEnd} >=  business_Hours_start


	   AND EXTRACT(YEAR FROM business_Hours_start) = #{dateYear}

	group by industry_involved

select IFNULL(SUM(registered_capital),0) from company where del_flag = '0'

	 AND business_Hours_start >= #{dateStart}


	 AND  #{dateEnd} >=  business_Hours_start


   AND EXTRACT(YEAR FROM business_Hours_start) = #{dateYear}
